import React, { useState } from "react";

const ResearchTasks = () => {
  const [timeRange, setTimeRange] = useState("Last Month");
  const [productsToFind, setProductsToFind] = useState(10);
  const [includeRevenueAnalysis, setIncludeRevenueAnalysis] = useState(true);
  const [verifyWithJournals, setVerifyWithJournals] = useState(true);
  const [scientificJournals, setScientificJournals] = useState({
    pubmed: true,
    nature: true,
    science: true,
    cell: true,
    lancet: true,
    nejm: true,
    jama: true,
  });
  const [claimsToAnalyze, setClaimsToAnalyze] = useState(50);
  const [newJournal, setNewJournal] = useState("");
  const [showAddSection, setShowAddSection] = useState(false);

  const handleJournalChange = (journal) => {
    setScientificJournals({
      ...scientificJournals,
      [journal]: !scientificJournals[journal],
    });
  };

  const handleSelectAll = () => {
    setScientificJournals(
      Object.keys(scientificJournals).reduce((acc, key) => {
        acc[key] = true;
        return acc;
      }, {})
    );
  };

  const handleDeselectAll = () => {
    setScientificJournals(
      Object.keys(scientificJournals).reduce((acc, key) => {
        acc[key] = false;
        return acc;
      }, {})
    );
  };

  const handleAddJournal = () => {
    if (newJournal.trim() && !scientificJournals[newJournal]) {
      setScientificJournals((prevState) => ({
        ...prevState,
        [newJournal]: true,
      }));
      setNewJournal("");
    }
  };

  return (
    <div className="bg-gray-900 text-gray-100 p-8 rounded-xl shadow-lg max-w-5xl mx-auto space-y-8">
      <h2 className="text-4xl font-extrabold text-center">Research Tasks</h2>

      {/* Research Configuration */}
      <div className="bg-gray-800 p-6 rounded-xl">
        <h3 className="text-2xl font-semibold mb-4">Research Configuration</h3>

        <div className="grid grid-cols-2 gap-4 mb-6">
          <button
            className={`px-4 py-2 rounded-lg font-medium transition-all ${
              timeRange === "Specific Influencer"
                ? "bg-green-600 text-white"
                : "bg-gray-700 hover:bg-gray-600"
            }`}
            onClick={() => setTimeRange("Specific Influencer")}
          >
            Specific Influencer
          </button>
          <button
            className={`px-4 py-2 rounded-lg font-medium transition-all ${
              timeRange === "Discover New"
                ? "bg-green-600 text-white"
                : "bg-gray-700 hover:bg-gray-600"
            }`}
            onClick={() => setTimeRange("Discover New")}
          >
            Discover New
          </button>
        </div>

        <div>
          <h4 className="font-medium mb-2">Time Range</h4>
          <div className="flex flex-wrap gap-2">
            {["Last Week", "Last Month", "Last Year", "All Time"].map((range) => (
              <button
                key={range}
                className={`px-4 py-2 rounded-lg font-medium transition-all ${
                  timeRange === range
                    ? "bg-blue-600 text-white"
                    : "bg-gray-700 hover:bg-gray-600"
                }`}
                onClick={() => setTimeRange(range)}
              >
                {range}
              </button>
            ))}
          </div>
        </div>
      </div>

      {/* Products Configuration */}
      <div className="bg-gray-800 p-6 rounded-xl">
        <h3 className="text-2xl font-semibold mb-4">Products Configuration</h3>

        <div className="mb-6">
          <label className="block font-medium mb-2">Products to Find Per Influencer</label>
          <input
            type="number"
            className="w-24 bg-gray-700 border border-gray-600 rounded-lg p-2 text-gray-100 focus:ring-2 focus:ring-blue-500"
            value={productsToFind}
            onChange={(e) => setProductsToFind(parseInt(e.target.value) || 0)}
          />
        </div>

        <div className="flex flex-col gap-4 items-end">
          <label className="inline-flex items-center justify-between w-full">
            <span className="ml-2">Include Revenue Analysis</span>
            <div
              className={`relative w-12 h-6 flex items-center bg-gray-700 rounded-full cursor-pointer transition-all ${
                includeRevenueAnalysis ? "bg-green-500" : "bg-gray-700"
              }`}
              onClick={() => setIncludeRevenueAnalysis(!includeRevenueAnalysis)}
            >
              <span
                className={`absolute left-1 top-1 w-4 h-4 bg-white rounded-full shadow transform transition-transform ${
                  includeRevenueAnalysis ? "translate-x-6" : "translate-x-0"
                }`}
              ></span>
            </div>
          </label>

          <label className="inline-flex items-center justify-between w-full">
            <span className="ml-2">Verify with Scientific Journals</span>
            <div
              className={`relative w-12 h-6 flex items-center bg-gray-700 rounded-full cursor-pointer transition-all ${
                verifyWithJournals ? "bg-green-500" : "bg-gray-700"
              }`}
              onClick={() => setVerifyWithJournals(!verifyWithJournals)}
            >
              <span
                className={`absolute left-1 top-1 w-4 h-4 bg-white rounded-full shadow transform transition-transform ${
                  verifyWithJournals ? "translate-x-6" : "translate-x-0"
                }`}
              ></span>
            </div>
          </label>
        </div>
      </div>

      {/* Scientific Journals Configuration */}
      {verifyWithJournals && (
        <div className="bg-gray-800 p-6 rounded-xl">
          <h3 className="text-2xl font-semibold mb-4">Scientific Journals</h3>

          <div className="flex justify-end items-center space-x-4 mb-6">
            <button
              className="text-sm text-green-500 hover:underline"
              onClick={handleSelectAll}
            >
              Select All
            </button>
            <button
              className="text-sm text-red-500 hover:underline"
              onClick={handleDeselectAll}
            >
              Deselect All
            </button>
          </div>

          <div className="grid grid-cols-2 gap-4 mb-6">
            {Object.keys(scientificJournals).map((journal) => (
              <label
                key={journal}
                className={`flex justify-between items-center bg-gray-700 p-2 rounded-lg cursor-pointer border transition-all ${
                  scientificJournals[journal] ? "border-green-500" : "border-transparent"
                }`}
                onClick={() => handleJournalChange(journal)}
              >
                <span className="text-sm font-medium capitalize">{journal}</span>
                <div
                  className={`h-5 w-5 rounded-full border-2 flex items-center justify-center transition-all ${
                    scientificJournals[journal] ? "bg-green-500 border-green-500" : "border-gray-500"
                  }`}
                >
                  {scientificJournals[journal] && (
                    <div className="h-2.5 w-2.5 rounded-full bg-white"></div>
                  )}
                </div>
              </label>
            ))}
          </div>

          {showAddSection && (
            <div className="space-y-2">
              <label className="block font-medium text-sm" htmlFor="add-journal">
                Add New Journal
              </label>
              <div className="flex space-x-2">
                <input
                  id="add-journal"
                  type="text"
                  className="flex-1 bg-gray-700 bg-opacity-50 border border-gray-600 rounded-lg p-3 text-gray-100 focus:ring-2 focus:ring-green-500"
                  placeholder="Journal name"
                  value={newJournal}
                  onChange={(e) => setNewJournal(e.target.value)}
                />
                <button
                  className="bg-green-600 hover:bg-green-700 text-white font-bold py-2 px-4 rounded-lg transition"
                  onClick={handleAddJournal}
                >
                  Add
                </button>
              </div>
            </div>
          )}

          <div className="mt-4">
            <button
              className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg transition"
              onClick={() => setShowAddSection(!showAddSection)}
            >
              {showAddSection ? "Remove Add Section" : "+ Add New Journal"}
            </button>
          </div>
        </div>
      )}

      {/* Claims Configuration */}
      <div className="bg-gray-800 p-6 rounded-xl">
        <h3 className="text-2xl font-semibold mb-4">Claims Configuration</h3>

        <div className="mb-6">
          <label className="block font-medium mb-2">Claims to Analyze Per Influencer</label>
          <input
            type="number"
            className="w-24 bg-gray-700 border border-gray-600 rounded-lg p-2 text-gray-100 focus:ring-2 focus:ring-blue-500"
            value={claimsToAnalyze}
            onChange={(e) => setClaimsToAnalyze(parseInt(e.target.value) || 0)}
          />
        </div>
      </div>

      <div className="flex justify-end">
        <button className="bg-green-600 hover:bg-green-700 text-white font-bold py-3 px-6 rounded-lg transition">
          + Start Research
        </button>
      </div>
    </div>
  );
};

export default ResearchTasks;
