import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const InfluencerDetail = () => {
  const { id } = useParams();
  const [influencer, setInfluencer] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchInfluencer = async () => {
      try {
        const response = await fetch(`http://localhost:3000/api/influencers/${id}`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setInfluencer(data);
      } catch (error) {
        console.error("Error fetching influencer details:", error);
        setError("Failed to fetch influencer details. Please try again later.");
      }
    };
    fetchInfluencer();
  }, [id]);

  if (error) {
    return <div className="bg-red-500 text-white p-4 rounded-lg">{error}</div>;
  }

  if (!influencer) {
    return <div className="text-white">Loading...</div>;
  }

  return (
    <div className="bg-gray-900 text-white min-h-screen p-6">
      <div className="max-w-7xl mx-auto">
        <div className="flex items-center mb-8">
          <img
            src={influencer.image}
            alt={influencer.name}
            className="w-24 h-24 rounded-full mr-6"
          />
          <div>
            <h1 className="text-3xl font-bold mb-2">{influencer.name}</h1>
            <div className="flex flex-wrap gap-2">
              {influencer.tags.map((tag, index) => (
                <span
                  key={index}
                  className="bg-teal-500 text-white px-3 py-1 rounded-lg text-sm"
                >
                  {tag}
                </span>
              ))}
            </div>
            <p className="text-gray-400 mt-2">{influencer.bio}</p>
          </div>
        </div>

        <div className="grid grid-cols-4 gap-6 mb-6">
          <div className="bg-gray-800 p-4 rounded-lg text-center">
            <h2 className="text-2xl font-bold">{influencer.trustScore}%</h2>
            <p className="text-gray-400">Trust Score</p>
          </div>
          <div className="bg-gray-800 p-4 rounded-lg text-center">
            <h2 className="text-2xl font-bold">${influencer.yearlyRevenue}M</h2>
            <p className="text-gray-400">Yearly Revenue</p>
          </div>
          <div className="bg-gray-800 p-4 rounded-lg text-center">
            <h2 className="text-2xl font-bold">{influencer.products}</h2>
            <p className="text-gray-400">Products</p>
          </div>
          <div className="bg-gray-800 p-4 rounded-lg text-center">
            <h2 className="text-2xl font-bold">{influencer.followers}+</h2>
            <p className="text-gray-400">Followers</p>
          </div>
        </div>

        <div className="border-b border-gray-700 mb-6">
          <nav className="flex space-x-6">
            {['Claims Analysis', 'Recommended Products', 'Monetization'].map((tab) => (
              <button
                key={tab}
                className="text-teal-500 font-semibold py-2 border-b-2 border-teal-500"
              >
                {tab}
              </button>
            ))}
          </nav>
        </div>

        <div className="bg-gray-800 p-6 rounded-lg">
          <div className="mb-4 flex justify-between items-center">
            <input
              type="text"
              placeholder="Search claims..."
              className="w-full bg-gray-700 text-gray-300 p-2 rounded-lg focus:outline-none focus:ring focus:ring-teal-500"
            />
          </div>

          <div className="flex flex-wrap gap-4 mb-4">
            {['All Categories', 'Sleep', 'Performance', 'Hormones', 'Nutrition', 'Exercise', 'Stress', 'Cognition', 'Motivation', 'Recovery', 'Mental Health'].map((category) => (
              <button
                key={category}
                className="bg-gray-700 px-4 py-2 rounded-lg text-gray-300 hover:bg-teal-500 hover:text-white"
              >
                {category}
              </button>
            ))}
          </div>

          <div className="flex justify-between mb-4">
            <div className="flex space-x-4">
              {['All Statuses', 'Verified', 'Questionable', 'Debunked'].map((status) => (
                <button
                  key={status}
                  className="bg-gray-700 px-4 py-2 rounded-lg text-gray-300 hover:bg-teal-500 hover:text-white"
                >
                  {status}
                </button>
              ))}
            </div>
            <select
              className="bg-gray-700 text-gray-300 px-4 py-2 rounded-lg focus:outline-none focus:ring focus:ring-teal-500"
            >
              <option>Date</option>
              <option>Trust Score</option>
            </select>
          </div>

          <div>
            {influencer.claims.map((claim, index) => (
              <div
                key={index}
                className="bg-gray-900 p-4 mb-4 rounded-lg border border-gray-700"
              >
                <div className="flex justify-between items-center">
                  <span
                    className={`px-3 py-1 rounded-lg text-sm font-semibold ${
                      claim.status === 'verified'
                        ? 'bg-green-500 text-white'
                        : 'bg-red-500 text-white'
                    }`}
                  >
                    {claim.status}
                  </span>
                  <span className="text-gray-400 text-sm">{claim.date}</span>
                </div>
                <h3 className="text-lg font-bold text-white mt-2">
                  {claim.title}
                </h3>
                <p className="text-gray-400 mt-2">{claim.description}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default InfluencerDetail;
