import React, { useState } from "react";
import { Link } from "react-router-dom";

const ResearchTasks = () => {
  const [influencerName, setInfluencerName] = useState("");
  const [estimatedFollowers, setEstimatedFollowers] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleStartResearch = async () => {
    if (!influencerName.trim()) {
      setError("Influencer name is required.");
      return;
    }

    setLoading(true);
    setError(null);

    const requestData = { influencerName };

    try {
      const response = await fetch("http://localhost:5000/api/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(requestData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(
          errorData.error || `Failed with status code ${response.status}`
        );
      }

      const data = await response.json();
      setEstimatedFollowers(data.estimatedFollowers);
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-gray-100 min-h-screen p-6">
      <div className="max-w-4xl mx-auto">
        <Link to="/dashboard" className="text-teal-600 hover:underline mb-4 inline-block">
          &larr; Back to Dashboard
        </Link>
        <h1 className="text-3xl font-bold text-gray-900 mb-6">
          Research Tasks
        </h1>
        <div className="bg-white shadow-lg rounded-lg p-6">
          <input
            type="text"
            placeholder="Enter influencer name"
            value={influencerName}
            onChange={(e) => setInfluencerName(e.target.value)}
            className="w-full mb-4 border border-gray-300 p-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-teal-500 text-black"
          />
          <div className="flex justify-end mt-6">
            <button
              onClick={handleStartResearch}
              className="bg-teal-600 text-white py-2 px-4 rounded-lg hover:bg-teal-700 transition duration-300"
              disabled={loading}
            >
              {loading ? "Loading..." : "+ Start Research"}
            </button>
          </div>
          {error && (
            <div className="bg-red-100 text-red-800 p-4 rounded-lg mt-4">
              {error}
            </div>
          )}
          {estimatedFollowers !== null && (
            <div className="bg-white shadow-lg rounded-lg p-6 mt-6">
              <h2 className="text-xl font-bold text-gray-900 mb-4">
                Estimated Followers
              </h2>
              <p className="text-gray-800">{estimatedFollowers}</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ResearchTasks;
