import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const InfluencerDetails = () => {
  const { id } = useParams();
  const [influencer, setInfluencer] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchInfluencer = async () => {
      try {
        const response = await fetch(`http://localhost:3000/api/influencers/${id}`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setInfluencer(data);
      } catch (error) {
        console.error("Error fetching influencer details:", error);
        setError("Failed to fetch influencer details. Please try again later.");
      }
    };
    fetchInfluencer();
  }, [id]);

  return (
    <div className="bg-gray-100 min-h-screen p-6">
      <div className="max-w-4xl mx-auto">
        {error ? (
          <div className="bg-red-100 text-red-800 p-4 rounded-lg">
            {error}
          </div>
        ) : (
          influencer && (
            <div className="bg-white shadow-lg rounded-lg p-6">
              <h1 className="text-3xl font-bold text-gray-800 mb-4">Details for Influencer ID: {id}</h1>
              <p className="text-gray-700 mb-2"><strong>Name:</strong> {influencer.name}</p>
              <p className="text-gray-700 mb-2"><strong>Followers:</strong> {influencer.followers.toLocaleString()}</p>
              <p className="text-gray-700 mb-2"><strong>Engagement:</strong> {influencer.engagement}%</p>
              <p className="text-gray-700 mb-2"><strong>Uploads:</strong> {influencer.uploads.toLocaleString()}</p>
              <p className="text-gray-700 mb-2"><strong>Description:</strong> {influencer.description}</p>
            </div>
          )
        )}
      </div>
    </div>
  );
};

export default InfluencerDetails;
