import React, { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";

const Navbar = () => {
  return (
    <nav className="bg-gray-900 text-white p-4">
      <div className="container mx-auto flex justify-between items-center">
        <div className="text-lg font-bold text-teal-400">VerifyInfluencers</div>
        <div className="flex space-x-4">
          <a href="/dashboard" className="hover:text-gray-300">Dashboard</a>
          <a href="/leaderboard" className="hover:text-gray-300">Leaderboard</a>
          <a href="/products" className="hover:text-gray-300">Products</a>
          <a href="/monetization" className="hover:text-gray-300">Monetization</a>
          <a href="/about" className="hover:text-gray-300">About</a>
          <a href="/contact" className="hover:text-gray-300">Contact</a>
          <a href="/admin" className="hover:text-gray-300">Admin</a>
          <a href="/logout" className="hover:text-gray-300">Sign Out</a>
        </div>
      </div>
    </nav>
  );
};

const ResearchTasks = () => {
  const [influencerName, setInfluencerName] = useState("");
  const [claimsToAnalyze, setClaimsToAnalyze] = useState(50);
  const [selectedJournals, setSelectedJournals] = useState(["PubMed Central", "Nature"]);
  const [includeRevenueAnalysis, setIncludeRevenueAnalysis] = useState(true);
  const [verifyWithJournals, setVerifyWithJournals] = useState(true);
  const [timeRange, setTimeRange] = useState("Last Month");

  const handleStartResearch = () => {
    // Placeholder for the backend integration to start research tasks
    console.log({ influencerName, claimsToAnalyze, selectedJournals, includeRevenueAnalysis, verifyWithJournals, timeRange });
  };

  return (
    <div className="bg-gray-100 min-h-screen">
      <Navbar />
      <div className="p-6 max-w-4xl mx-auto">
        <h1 className="text-2xl font-bold text-gray-800 mb-4">Research Tasks</h1>
        <Card className="bg-white shadow-md rounded-lg">
          <CardContent>
            <div className="grid grid-cols-2 gap-4 mb-4">
              <Button
                variant={timeRange === "Last Week" ? "default" : "outline"}
                onClick={() => setTimeRange("Last Week")}
                className={timeRange === "Last Week" ? "bg-teal-400 text-white" : "border-gray-300 text-gray-800"}
              >
                Last Week
              </Button>
              <Button
                variant={timeRange === "Last Month" ? "default" : "outline"}
                onClick={() => setTimeRange("Last Month")}
                className={timeRange === "Last Month" ? "bg-teal-400 text-white" : "border-gray-300 text-gray-800"}
              >
                Last Month
              </Button>
              <Button
                variant={timeRange === "Last Year" ? "default" : "outline"}
                onClick={() => setTimeRange("Last Year")}
                className={timeRange === "Last Year" ? "bg-teal-400 text-white" : "border-gray-300 text-gray-800"}
              >
                Last Year
              </Button>
              <Button
                variant={timeRange === "All Time" ? "default" : "outline"}
                onClick={() => setTimeRange("All Time")}
                className={timeRange === "All Time" ? "bg-teal-400 text-white" : "border-gray-300 text-gray-800"}
              >
                All Time
              </Button>
            </div>
            <Input
              placeholder="Enter influencer name"
              value={influencerName}
              onChange={(e) => setInfluencerName(e.target.value)}
              className="mb-4 border-gray-300"
            />
            <Input
              type="number"
              placeholder="Claims to analyze per influencer"
              value={claimsToAnalyze}
              onChange={(e) => setClaimsToAnalyze(parseInt(e.target.value) || 0)}
              className="mb-4 border-gray-300"
            />
            <div className="flex gap-4 items-center mb-4">
              <label className="flex items-center gap-2 text-gray-800">
                <input
                  type="checkbox"
                  checked={includeRevenueAnalysis}
                  onChange={() => setIncludeRevenueAnalysis(!includeRevenueAnalysis)}
                  className="form-checkbox text-teal-400"
                />
                Include Revenue Analysis
              </label>
              <label className="flex items-center gap-2 text-gray-800">
                <input
                  type="checkbox"
                  checked={verifyWithJournals}
                  onChange={() => setVerifyWithJournals(!verifyWithJournals)}
                  className="form-checkbox text-teal-400"
                />
                Verify with Scientific Journals
              </label>
            </div>
            <Button onClick={handleStartResearch} className="w-full bg-teal-400 text-white hover:bg-teal-500">
              Start Research
            </Button>
          </CardContent>
        </Card>
      </div>
    </div>
  );
};

export default ResearchTasks;
