import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Leaderboard = () => {
  const [influencers, setInfluencers] = useState([]);

  useEffect(() => {
    const fetchInfluencers = async () => {
      const response = await fetch('http://localhost:3000/api/influencers');
      const data = await response.json();
      setInfluencers(data);
    };
    fetchInfluencers();
  }, []);

  return (
    <div className="bg-gray-100 min-h-screen p-6">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-900 mb-6">Leaderboard</h1>
        <div className="bg-white shadow-lg rounded-lg p-6">
          <table className="min-w-full bg-white">
            <thead>
              <tr>
                <th className="py-2 text-left text-gray-900">Influencer</th>
                <th className="py-2 text-left text-gray-900">Trust Score</th>
                <th className="py-2 text-left text-gray-900">Followers</th>
                <th className="py-2 text-left text-gray-900">Claims</th>
              </tr>
            </thead>
            <tbody>
              {influencers.map((influencer, index) => (
                <tr key={index} className="border-t">
                  <td className="py-2">
                    <Link to={`/influencer/${influencer.name}`} className="text-teal-500 hover:underline">
                      {influencer.name}
                    </Link>
                  </td>
                  <td className="py-2">{influencer.trustScore}</td>
                  <td className="py-2">{influencer.followers}</td>
                  <td className="py-2">{influencer.claims}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Leaderboard;
