import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Search, BarChart, DollarSign, User, Package } from "lucide-react";

const InfluencerDetails = () => {
  const { id } = useParams();
  const [activeTab, setActiveTab] = useState("claims");
  const [influencer, setInfluencer] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchInfluencerDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/chat/influencer/${id}`);
        if (!response.ok) {
          if (response.status === 404) {
            throw new Error("Influencer not found");
          } else {
            throw new Error(`HTTP error! Status: ${response.status}`);
          }
        }
        const data = await response.json();
        setInfluencer(data);
      } catch (error) {
        console.error("Error fetching influencer details:", error);
        setError(error.message);
      }
    };

    fetchInfluencerDetails();
  }, [id]);

  if (error) {
    return <div className="bg-red-500 text-white p-4 rounded-md">{error}</div>;
  }

  if (!influencer) {
    return <div className="text-center p-4">Loading...</div>;
  }

  return (
    <div className="bg-gradient-to-b from-green-50 to-green-100 min-h-screen p-6">
      <div className="container mx-auto max-w-5xl">
        {/* Profile Header */}
        <div className="flex items-center space-x-4 p-6 bg-white rounded-lg shadow-lg">
          <img
            src={influencer.profileImage || "https://via.placeholder.com/80"}
            alt="Profile"
            className="rounded-full w-20 h-20 border-2 border-teal-500"
          />
          <div>
            <h1 className="text-2xl font-bold">{influencer.influencerName}</h1>
            <p className="text-gray-600">{influencer.bio || "No bio available"}</p>
          </div>
        </div>

        {/* Stats Section */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mt-6">
          <div className="bg-white p-4 rounded-lg text-center shadow-lg">
            <BarChart size={24} className="mx-auto text-teal-400" />
            <p className="text-lg font-semibold mt-2">{influencer.trustScore}%</p>
            <p className="text-gray-600 text-sm">Trust Score</p>
          </div>
          <div className="bg-white p-4 rounded-lg text-center shadow-lg">
            <DollarSign size={24} className="mx-auto text-green-400" />
            <p className="text-lg font-semibold mt-2">${influencer.yearlyRevenue}</p>
            <p className="text-gray-600 text-sm">Yearly Revenue</p>
          </div>
          <div className="bg-white p-4 rounded-lg text-center shadow-lg">
            <Package size={24} className="mx-auto text-blue-400" />
            <p className="text-lg font-semibold mt-2">{influencer.recommendedProducts.length}</p>
            <p className="text-gray-600 text-sm">Recommended Products</p>
          </div>
          <div className="bg-white p-4 rounded-lg text-center shadow-lg">
            <User size={24} className="mx-auto text-purple-400" />
            <p className="text-lg font-semibold mt-2">{influencer.followers}</p>
            <p className="text-gray-600 text-sm">Followers</p>
          </div>
        </div>

        {/* Tabs Navigation */}
        <div className="flex mt-8 space-x-6 border-b border-gray-300 pb-2">
          {[
            { id: "claims", label: "Claims Analysis" },
            { id: "products", label: "Recommended Products" },
            { id: "monetization", label: "Monetization" },
          ].map((tab) => (
            <button
              key={tab.id}
              className={`text-lg pb-2 font-medium transition ${
                activeTab === tab.id
                  ? "border-b-2 border-teal-400 text-teal-400"
                  : "text-gray-600 hover:text-teal-300"
              }`}
              onClick={() => setActiveTab(tab.id)}
            >
              {tab.label}
            </button>
          ))}
        </div>

        {/* Tab Content */}
        <div className="mt-6">
          {activeTab === "claims" && (
            <div className="bg-white p-4 rounded-lg shadow-lg">
              <h2 className="text-xl font-semibold mb-4">Latest Verified Claims</h2>
              <div className="flex items-center bg-gray-100 p-2 rounded-lg">
                <Search size={20} className="text-gray-400" />
                <input
                  type="text"
                  placeholder="Search claims..."
                  className="bg-transparent ml-2 outline-none text-gray-700 w-full"
                />
              </div>
              <p className="mt-4 text-gray-600">Showing latest claims with analysis.</p>
              <table className="table-auto w-full bg-white shadow rounded-lg mt-4">
                <thead>
                  <tr className="bg-gray-200">
                    <th className="p-2">Claim</th>
                    <th className="p-2">Verification Status</th>
                    <th className="p-2">Confidence Score</th>
                  </tr>
                </thead>
                <tbody>
                  {influencer.claims.map((claim, index) => (
                    <tr key={index} className="border-t">
                      <td className="p-2">{claim.text}</td>
                      <td className="p-2">{claim.status}</td>
                      <td className="p-2">{claim.confidenceScore}%</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
          {activeTab === "products" && (
            <div className="bg-white p-4 rounded-lg shadow-lg">
              <h2 className="text-xl font-semibold mb-4">Recommended Products</h2>
              {influencer.recommendedProducts.length > 0 ? (
                <ul className="list-disc list-inside text-gray-600">
                  {influencer.recommendedProducts.map((product, index) => (
                    <li key={index}>{product}</li>
                  ))}
                </ul>
              ) : (
                <p className="text-gray-600">No recommended products available.</p>
              )}
            </div>
          )}
          {activeTab === "monetization" && (
            <div className="bg-white p-4 rounded-lg shadow-lg">
              <h2 className="text-xl font-semibold mb-4">Monetization Insights</h2>
              <p className="text-gray-600">Revenue trends and growth strategies.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default InfluencerDetails;
