import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Dashboard = () => {
  const [summary, setSummary] = useState({ totalInfluencers: 0, totalClaims: 0, verifiedClaims: 0 });
  const [leaderboard, setLeaderboard] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSummary = async () => {
      try {
        const response = await fetch("http://localhost:3000/api/summary");
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setSummary(data);
      } catch (error) {
        console.error("Error fetching summary:", error);
        setError("Failed to fetch summary. Please try again later.");
      }
    };

    const fetchLeaderboard = async () => {
      try {
        const response = await fetch("http://localhost:3000/api/leaderboard");
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setLeaderboard(data);
      } catch (error) {
        console.error("Error fetching leaderboard:", error);
        setError("Failed to fetch leaderboard. Please try again later.");
      }
    };

    fetchSummary();
    fetchLeaderboard();
  }, []);

  return (
    <div className="bg-gray-100 min-h-screen p-6">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-4xl font-bold mb-4">Dashboard</h1>
        <p className="text-gray-400 mb-8">
          Overview of the health influencers and their claims.
        </p>

        {error ? (
          <div className="bg-red-500 text-white p-4 rounded-lg">{error}</div>
        ) : (
          <>
            <div className="grid grid-cols-3 gap-6 mb-6">
              <div className="bg-white p-6 rounded-lg shadow-lg text-center">
                <h2 className="text-3xl font-bold">{summary.totalInfluencers}</h2>
                <p className="text-gray-400">Total Influencers</p>
              </div>
              <div className="bg-white p-6 rounded-lg shadow-lg text-center">
                <h2 className="text-3xl font-bold">{summary.totalClaims}</h2>
                <p className="text-gray-400">Total Claims</p>
              </div>
              <div className="bg-white p-6 rounded-lg shadow-lg text-center">
                <h2 className="text-3xl font-bold">{summary.verifiedClaims}</h2>
                <p className="text-gray-400">Verified Claims</p>
              </div>
            </div>

            <div className="bg-white p-6 rounded-lg shadow-lg mb-6">
              <h2 className="text-2xl font-bold mb-4">Leaderboard</h2>
              <ul>
                {leaderboard.map((item, index) => (
                  <li key={index} className="mb-2">
                    {item.name}: {item.points} points
                  </li>
                ))}
              </ul>
            </div>
          </>
        )}

        <div className="flex justify-between items-center mb-6">
          <Link to="/leaderboard" className="bg-teal-500 px-6 py-3 rounded-lg text-white font-semibold shadow-md hover:bg-teal-600 transition duration-300">
            View Leaderboard
          </Link>
          <Link to="/research-tasks" className="bg-teal-500 px-6 py-3 rounded-lg text-white font-semibold shadow-md hover:bg-teal-600 transition duration-300">
            Start Research
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
